import React, { useState } from "react";
import { AiFillPlusCircle } from "react-icons/ai";
import axios from "axios";

const Button = ({ setUpdateUI }) => {
  const [photoTitle, setPhotoTitle] = useState(""); // State to store the title

  const handleChange = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("photo", e.target.files[0]);
    formData.append("title", photoTitle); // Use the stored photo title
    axios
      .post("http://localhost:5000/api/save", formData)
      .then((res) => {
        console.log(res.data);
        setUpdateUI(res.data._id);
      })
      .catch((err) => console.log(err));
  };

  return (
    <label className="button" htmlFor="file_picker">
      <AiFillPlusCircle />
      <input
        hidden
        type="file"
        name="file_picker"
        id="file_picker"
        onChange={(e) => handleChange(e)}
      />
      <input
        type="text"
        placeholder="Enter photo title"
        value={photoTitle}
        onChange={(e) => setPhotoTitle(e.target.value)}
      />
    </label>
  );
};

export default Button;
